template.script:
function showHideFieldsets() {
var assetName = document.getElementById('asset_name').value;
var buildingFieldset = document.getElementById('buildingFieldset');
var vehicleFieldset = document.getElementById('vehicleFieldset');
var landFieldset = document.getElementById('landFieldset');
console.log("dfgh", assetName);

buildingFieldset.style.display = (assetName === '1') ? 'block' : 'none';
vehicleFieldset.style.display = (assetName === '2') ? 'block' : 'none';
landFieldset.style.display = (assetName === '3') ? 'block' : 'none';
}
window.onload = function () {
var deleteTarget = document.getElementById("deleteID");
if(deleteTarget){
deleteValue=deleteTarget.value;

var deleteBtn = document.querySelectorAll('.delete');
deleteBtn.forEach(e => {
e.addEventListener('click', () => {
Swal.fire({
title: "Are you sure?",
text: "You won't be able to revert this!",
icon: "warning",
showCancelButton: true,
confirmButtonColor: "#3085d6",
cancelButtonColor: "#d33",
confirmButtonText: "Yes, delete it!"
}).then((result) => {
if (result.isConfirmed) {
setTimeout(deleteAsset(deleteValue), 2000);
Swal.fire({
title: "Deleted!",
text: "Your file has been deleted.",
icon: "success"
});
}
});
});
});

}

function deleteAsset(deleteTarget){
var url = '/organization/fix-assets/delete/' + deleteTarget;
var method = 'DELETE';
fetch(url, {
method: method
})
.then(response => {
if (!response.ok) {
throw new Error('Network response was not ok');
}

// Handle successful deletion
setTimeout(function(){
window.location.href = `/organization/fix-assets`;
},2000);
});
}

}

function showHideFieldsets() {
var assetName = document.getElementById('asset_name').value;
var buildingFieldset = document.getElementById('buildingFieldset');
var vehicleFieldset = document.getElementById('vehicleFieldset');
var landFieldset = document.getElementById('landFieldset');
console.log("dfgh", assetName);


buildingFieldset.style.display = (assetName === '1') ? 'block' : 'none';
vehicleFieldset.style.display = (assetName === '2') ? 'block' : 'none';
