t.script:
function updateDistricts() {
	var provinceSelect = document.getElementById('province');
	var districtSelect = document.getElementById('district');

	// Get the selected province ID
	var selectedProvinceId = provinceSelect.value;

	// Call Odoo RPC to fetch districts based on the selected province
	odoo.jsonRpc("/update_districts", 'call', {'province_id': selectedProvinceId}).then(function(districts) {
		// Clear existing options
		districtSelect.innerHTML = "";

		// Populate the district options based on the response
		districts.forEach(function(district) {
			var option = document.createElement('option');
			option.value = district.id;
			option.text = district.district_name_np;
			districtSelect.appendChild(option);
		});
	});
}
