t.script:
window.onload = function () {

var deleteTarget = document.getElementById("deleteID");
if(deleteTarget){
deleteValue=deleteTarget.value;

var deleteBtn = document.querySelectorAll('.delete');
deleteBtn.forEach(e => {
e.addEventListener('click', () => {
Swal.fire({
title: "Are you sure?",
text: "You won't be able to revert this!",
icon: "warning",
showCancelButton: true,
confirmButtonColor: "#3085d6",
cancelButtonColor: "#d33",
confirmButtonText: "Yes, delete it!"
}).then((result) => {
if (result.isConfirmed) {
setTimeout(deleteMember(deleteValue), 2000);
Swal.fire({
title: "Deleted!",
text: "Your file has been deleted.",
icon: "success"
});
}
});
});
});

}

function deleteMember(value){
var url = '/upabhokta-samiti/member-details/delete/' + value;
var method = 'DELETE';
fetch(url, {
method: method
})
.then(response => {
if (!response.ok) {
throw new Error('Network response was not ok');
}

// Handle successful deletion
setTimeout(function(){
window.location.href = `/upabhokta-samiti/member-details`;
},2000);
});
}

